changeSet.sql:
create table t_fraction
(
    id bigint not null,
    fraction varchar(150) not null,
    destruction int not null
);

create unique index fraction_destruction_uindex
on t_fraction (destruction);

create unique index fraction_fraction_uindex
on t_fraction (fraction);

create unique index fraction_id_uindex
on t_fraction (id);

alter table t_fraction
add constraint fraction_pk
primary key (id);

create table creature ( id bigint not null, unit_points int not null, points_sum int not null, movement float not null, fight varchar(10) not null, strength int not null, defense int not null, attack int not null, lives int not null, courage int not null, power int not null, will int not null, fortune int not null, option varchar(100), special_attack varchar(100), magic varchar(100), unit_type varchar(100) not null, fraction_id int not null constraint creature_t_fraction_id_fk references t_fraction (id) );
create unique index creature_id_uindex on creature (id);
alter table creature add constraint creature_pk primary key (id);
